definition.script:
pipeline {
    agent any

    stages {
        stage('Git Checkout') {
            steps {
                git url: 'https://github.com/dreadnone/sdvps-materials.git'
            }
        }

        stage('Test') {
            steps {
                sh '/usr/bin/go test .'
            }
        }

        stage('Build Docker Image') {
            steps {
                dir('/home/andrew/sdvps-materials') {
                    sh """
                        docker build . -t ubuntu-bionic:8082/hello-world:v${env.BUILD_NUMBER}
                    """
                }
            }
        }

        stage('Push Docker Image') {
            steps {
                dir('/home/andrew/sdvps-materials') {
                    sh """
                        docker login ubuntu-bionic:8082 -u admin -p admin
                        docker push ubuntu-bionic:8082/hello-world:v${env.BUILD_NUMBER}
                        docker logout
                    """
                }
            }
        }
    }
}
